mapper.update:
update bicycle
set bicycle_current_x = #{bicycleCurrentX,jdbcType=DOUBLE},
  bicycle_current_y = #{bicycleCurrentY,jdbcType=DOUBLE},
  bicycle_last_time = #{bicycleLastTime,jdbcType=TIMESTAMP},
  bicycle_statement = #{bicycleStatement,jdbcType=INTEGER}
where bicycle_id = #{bicycleId,jdbcType=INTEGER}
